"use strict";

var input = document.querySelector('.js-input-search');
var btnWrapper = document.querySelector('.js-btn-wrapper');
input.addEventListener('input', function () {
  if (input.value.length > 0) {
    btnWrapper.classList.add('active--wrapper');
  } else if (input.value.length === 0) {
    btnWrapper.classList.remove('active--wrapper');
  }
});
var btnClose = document.querySelector('.js-btn-close-search');
btnClose.addEventListener('click', function () {
  input.value = '';
});
var swiperHeader = new Swiper('.header__bottom-swiper', {
  slidesPerView: 'auto',
  spaceBetween: 4,
  freeMode: true,
  navigation: {
    nextEl: '.btn-next',
    prevEl: '.btn-prev'
  }
});
var btnActiveSlide = document.querySelectorAll('.header__bottom-slide');
btnActiveSlide.forEach(function (btn) {
  btn.addEventListener('click', function () {
    btn.classList.add('active-slide');
  });
});
var btnClearSlide = document.querySelectorAll('.header__bottom-btn');
btnClearSlide.forEach(function (btn) {
  btn.addEventListener('click', function (e) {
    e.stopPropagation();
    btn.parentElement.classList.remove('active-slide');
    console.log(btn.parentElement);
  });
});
var btnBuy = document.querySelectorAll('.product-card-btn-red');
btnBuy.forEach(function (btn) {
  btn.addEventListener('click', function () {
    btn.parentElement.classList.add('active-yollow');
  });
});
var modal = document.querySelector('.modal');
var btnOpenModal = document.querySelectorAll('.product-card-btn-yollow');
btnOpenModal.forEach(function (btn) {
  btn.addEventListener('click', function () {
    modal.classList.add('active-modal');
  });
});
modal.querySelector('.modal__dialog').addEventListener('click', function (event) {
  event._isClickWidthInModal = true;
});
modal.addEventListener('click', function (event) {
  if (event._isClickWidthInModal) return;
  modalClose(modal);
});
var btnCloseModal = document.querySelector('.modal__close-btn');
btnCloseModal.addEventListener('click', function () {
  return modalClose(modal);
});

function modalClose(modal) {
  modal.classList.remove('active-modal');
}

var countSpan = document.querySelector('.modal__count-num');
var plusBtn = document.querySelector('.modal__btn-plus');
var minusBtn = document.querySelector('.modal__btn-minus');
plusBtn.addEventListener('click', function () {
  var num = Number(countSpan.textContent);
  num++;
  countSpan.textContent = String(num);
});
minusBtn.addEventListener('click', function () {
  var num = Number(countSpan.textContent);

  if (num === 0) {
    num = 0;
    countSpan.textContent = String(num);
  } else {
    num--;
    countSpan.textContent = String(num);
  }
});
